<?phpnamespace Nemundo\Meteo\Dwd\Content\MapSlider;use Nemundo\Content\Type\AbstractContentType;use Nemundo\Meteo\Dwd\Data\MapContent\MapContent;use Nemundo\Meteo\Dwd\Data\MapContent\MapContentReader;use Nemundo\Meteo\Dwd\Data\MapContent\MapContentRow;use Nemundo\Meteo\Dwd\Data\MapContent\MapContentUpdate;class MapSliderContentType extends AbstractContentType{    public $mapId;    protected function loadContentType()    {        $this->typeLabel = 'DWD Map Slider';        $this->typeId = 'dd493511-e3cb-4874-8c7f-7083c8cdea1e';        $this->formClassList[] = MapSliderContentForm::class;        $this->viewClassList[]  = MapSliderContentView::class;    }    protected function onCreate()    {        $data=new MapContent();        $data->mapId=$this->mapId;        $this->dataId=$data->save();    }    protected function onUpdate()    {        $update=new MapContentUpdate();        $update->mapId=$this->mapId;        $update->updateById($this->dataId);    }    protected function onDataRow()    {        $reader=new MapContentReader();        $reader->model->loadMap();        $this->dataRow=$reader->getRowById($this->dataId);    }    /**     * @return \Nemundo\Model\Row\AbstractModelDataRow|MapContentRow     */    public function getDataRow()    {        return parent::getDataRow();    }    public function getSubject()    {        $subject=$this->getDataRow()->map->map .' (Slider)';        return $subject;    }}