<?phpnamespace Nemundo\Meteo\Dwd\Content\MapSlider;use Nemundo\Content\View\AbstractContentView;use Nemundo\Db\Sql\Order\SortOrder;use Nemundo\Meteo\Dwd\Data\MapImage\MapImageReader;use Nemundo\Package\Bootstrap\Carousel\BootstrapImageCarousel;class MapSliderContentView extends AbstractContentView{    /**     * @var MapSliderContentType     */    public $contentType;    public function getContent()    {        $slider = new BootstrapImageCarousel($this);        $slider->slideEffect = false;        $reader = new MapImageReader();        $reader->filter->andEqual($reader->model->mapId, $this->contentType->getDataRow()->mapId);        $reader->addOrder($reader->model->id, SortOrder::DESCENDING);        $reader->limit = 10;        foreach ($reader->getData() as $imageRow) {            $slider->addImage($imageRow->image->getImageUrl($reader->model->imageAutoSize400));        }        return parent::getContent();    }}