<?phpnamespace Nemundo\Meteo\Dwd\Data\MapContent;class MapContentReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var MapContentModel*/public $model;public function __construct() {parent::__construct();$this->model = new MapContentModel();}/*** @return MapContentRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return MapContentRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return MapContentRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new MapContentRow($dataRow, $this->model);$row->model = $this->model;return $row;}}