<?phpnamespace Nemundo\Meteo\Dwd\Data\MapImage;class MapImagePaginationReader extends \Nemundo\Model\Reader\AbstractPaginationModelDataReader {/*** @var MapImageModel*/public $model;public function __construct() {parent::__construct();$this->model = new MapImageModel();}/*** @return MapImageRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = new MapImageRow($dataRow, $this->model);$list[] = $row;}return $list;}}