<?phpnamespace Nemundo\Meteo\Dwd\Data\MapImage;class MapImageReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var MapImageModel*/public $model;public function __construct() {parent::__construct();$this->model = new MapImageModel();}/*** @return MapImageRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return MapImageRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return MapImageRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new MapImageRow($dataRow, $this->model);$row->model = $this->model;return $row;}}