<?phpnamespace Nemundo\Meteo\Dwd\Data\MapType;class MapTypeReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var MapTypeModel*/public $model;public function __construct() {parent::__construct();$this->model = new MapTypeModel();}/*** @return MapTypeRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return MapTypeRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return MapTypeRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new MapTypeRow($dataRow, $this->model);$row->model = $this->model;return $row;}}