<?phpnamespace Nemundo\Meteo\Dwd\Ftp;use Nemundo\Core\Base\DataSource\AbstractDataSource;use Nemundo\Core\Ftp\FtpReader;class DwdFtpReader extends AbstractDataSource{    /**     * @var string     */    public $path;    protected function loadData()    {        /*if (!$this->checkProperty('path')) {            return;        }*/        $ftp = new FtpReader();        $ftp->host = 'opendata.dwd.de';        $ftp->path=$this->path;        //$ftp->path = '/weather' . $this->path;        //$ftp->path = '/weather/charts/analysis/';        $ftp->scheme = 'https';        foreach ($ftp->getData() as $filename) {            $this->addItem($filename->url);        }    }}