<?phpnamespace Nemundo\Meteo\Emagramm\Content\Emagramm;use Nemundo\Meteo\Emagramm\Data\Emagramm\EmagrammReader;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;use Nemundo\Content\View\AbstractContentView;class EmagrammContentView extends AbstractContentView{    protected function loadView()    {        $this->viewName='default';        $this->viewId='aea4e716-19a1-4e6c-9a66-265187682b23';        $this->defaultView=true;    }    public function getContent()    {        $emagrammRow = (new EmagrammReader())->getRowById($this->contentType->getDataId());        $img = new BootstrapResponsiveImage($this);        $img->src = $emagrammRow->image->getUrl();        return parent::getContent();    }}