<?phpnamespace Nemundo\Meteo\Emagramm\Content\Latest;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Meteo\Emagramm\Com\ListBox\LocationListBox;class EmagrammLatestContentForm extends AbstractContentForm{    /**     * @var EmagrammLatestContentType     */    public $contentType;    /**     * @var LocationListBox     */    private $location;    public function getContent()    {        $this->location = new LocationListBox($this);        $this->location->validation = true;        return parent::getContent();    }    protected function loadUpdateForm()    {        $this->location->value = $this->contentType->getDataRow()->locationId;    }    protected function onSubmit()    {        $this->contentType->locationId = $this->location->getValue();        $this->contentType->saveType();    }}