<?phpnamespace Nemundo\Meteo\Emagramm\Content\Latest;use Nemundo\Content\Type\AbstractContentType;class EmagrammLatestContentType extends AbstractContentType{    public $locationId;    protected function loadContentType()    {        $this->typeLabel = 'Emagramm';        $this->typeId = '9c91ddd3-2fe8-4e01-aab6-692a7769871b';        $this->viewClassList[] = EmagrammLatestContentView::class;        $this->formClassList[] = EmagrammLatestContentForm::class;    }    protected function onCreate()    {        $data = new EmagrammCms();        $data->locationId = $this->locationId;        $this->dataId = $data->save();    }    protected function onUpdate()    {        $update = new EmagrammCmsUpdate();        $update->locationId = $this->locationId;        $update->updateById($this->dataId);    }    protected function onDataRow()    {        $reader = new EmagrammCmsReader();        $reader->model->loadLocation();        $this->dataRow = $reader->getRowById($this->dataId);    }    /**     * @return \Nemundo\Model\Row\AbstractModelDataRow|EmagrammCmsRow     */    public function getDataRow()    {        return parent::getDataRow();    }    public function getSubject()    {        $subject = $this->getDataRow()->location->location;        return $subject;    }}