<?phpnamespace Nemundo\Meteo\Emagramm\Crawler;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\File\UniqueFilename;use Nemundo\Core\Type\DateTime\DateTime;use Nemundo\Core\WebRequest\CurlWebRequest;use Nemundo\Project\Path\TmpPath;class EmagrammCrawlerItem extends AbstractBase{    /**     * @var string     */    public $location;    /**     * @var string     */    public $dateTimeText;    /**     * @var DateTime     */    public $dateTime;    /**     * @var string     */    public $url;    /**     * @var CurlWebRequest     */    private $webRequest;    public function __construct(CurlWebRequest $webRequest)    {        $this->webRequest = $webRequest;    }    public function downloadEmagramm()    {        $filename = (new TmpPath())            ->addPath((new UniqueFilename())->getUniqueFilename('gif'))            ->getFilename();        $this->webRequest->downloadUrl($this->url, $filename);        return $filename;    }}