<?phpnamespace Nemundo\Meteo\Emagramm\Script;use Nemundo\App\Script\Type\AbstractConsoleScript;use Nemundo\Meteo\Emagramm\Content\EmagrammContentType;use Nemundo\Meteo\Emagramm\Content\EmagrammSaveContentType;use Nemundo\Meteo\Emagramm\Data\Emagramm\EmagrammReader;use Nemundo\Meteo\Emagramm\Data\EmagrammCollection;use Nemundo\Meteo\Emagramm\Data\Location\LocationDelete;use Nemundo\Meteo\Emagramm\Install\EmagrammInstall;use Nemundo\Model\Setup\ModelCollectionSetup;use Nemundo\Process\App\Notification\Data\Category\CategoryDelete;class EmagrammCleanScript extends AbstractConsoleScript{    protected function loadScript()    {        $this->scriptName='emagramm-clean';    }    public function run()    {        $reader = new EmagrammReader();        foreach ($reader->getData() as $emagrammRow) {            (new EmagrammSaveContentType($emagrammRow->id))->deleteType();        }        (new ModelCollectionSetup())->removeCollection(new EmagrammCollection());        (new EmagrammInstall())->install();        //(new LocationDelete())->delete();    }}