<?phpnamespace Nemundo\Meteo\Eumetsat\Com\ListBox;use Nemundo\Meteo\Eumetsat\Data\Region\RegionReader;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class RegionListBox extends BootstrapListBox{    public function getContent()    {        $this->label='Region';        $reader=new RegionReader();        foreach ($reader->getData() as $regionRow) {            $this->addItem($regionRow->id,$regionRow->region);        }        return parent::getContent(); // TODO: Change the autogenerated stub    }}