<?phpnamespace Nemundo\Meteo\Eumetsat\Content\Latest;use Nemundo\Meteo\Eumetsat\Com\ListBox\ImageTypeListBox;use Nemundo\Meteo\Eumetsat\Com\ListBox\RegionListBox;use Nemundo\Process\Content\Form\AbstractContentForm;class EumetsatLatestContentForm extends AbstractContentForm{    /**     * @var EumetsatLatestContentType     */    public $contentType;    /**     * @var RegionListBox     */    private $region;    /**     * @var ImageTypeListBox     */    private $imageType;    public function getContent()    {        $this->region = new RegionListBox($this);        $this->region->validation=true;        $this->imageType=new ImageTypeListBox($this);        $this->imageType->validation=true;        return parent::getContent();    }    protected function loadUpdateForm()    {        $dataRow=$this->contentType->getDataRow();        $this->imageType->value=$dataRow->imageTypeId;        $this->region->value=$dataRow->regionId;    }    protected function onSubmit()    {        $this->contentType->regionId = $this->region->getValue();        $this->contentType->imageTypeId=$this->imageType->getValue();        $this->contentType->saveType();    }}