<?phpnamespace Nemundo\Meteo\Eumetsat\Content\Latest;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;use Nemundo\Process\Content\View\AbstractContentView;class EumetsatLatestContentView extends AbstractContentView{    /**     * @var EumetsatLatestContentType     */    public $contentType;    public function getContent()    {        $regionRow = $this->contentType->getDataRow();        $img = new BootstrapResponsiveImage($this);        $img->src = 'https://eumetview.eumetsat.int/static-images/latestImages/EUMETSAT_MSG_'.$regionRow->imageType->imageTypeName.'_' . $regionRow->region->region . '.jpg';        return parent::getContent();    }}