<?phpnamespace Nemundo\Meteo\Igra\Import;use Nemundo\Core\Csv\Reader\FixWidthCsvReader;use Nemundo\Core\WebRequest\CurlWebRequest;use Nemundo\Meteo\Base\Import\AbstractImport;use Nemundo\Meteo\Igra\Data\Country\Country;use Nemundo\Project\Path\TmpPath;class CountryImport extends AbstractImport{    public function importData()    {        $url = 'https://www1.ncdc.noaa.gov/pub/data/igra/igra2-country-list.txt';        $filename = (new TmpPath())            ->addPath('igra2-country-list.txt')            ->getFilename();        //(new CurlWebRequest())->downloadUrl($url, $filename);        $reader=new FixWidthCsvReader();        $reader->filename=$filename;        //$reader->addSeperationPosition(0);        $reader->addSeperationPosition(2);        foreach ($reader->getData() as $csvRow) {            $data=new Country();            $data->updateOnDuplicate=true;            $data->countryCode=$csvRow[0];            $data->country=$csvRow[1];            $data->save();        }    }}