<?phpnamespace Nemundo\Meteo\Isd\Com\Chart;use Nemundo\Core\Type\DateTime\Date;use Nemundo\Meteo\Isd\Data\Station\StationRow;use Nemundo\Package\Echarts\Chart\AbstractEchart;abstract class AbstractStationChart extends AbstractEchart{    /**     * @var Date     */    public $dateFrom;    /**     * @var Date     */    public $dateTo;    /**     * @var bool     */    public $showTimeOnXAxes = false;    /**     * @var StationRow[]     */    protected $stationRowList = [];    abstract protected function onStationRow(StationRow $stationRow);    public function addStationRow(StationRow $stationRow)    {        $this->stationRowList[] = $stationRow;        return $this;    }}