<?phpnamespace Nemundo\Meteo\Isd\Com\Chart\Day;use Nemundo\Meteo\Isd\Com\Chart\AbstractStationChart;use Nemundo\Meteo\Isd\Data\MeasurementDay\MeasurementDayReader;use Nemundo\Meteo\Isd\Data\MeasurementDay\MeasurementDayRow;abstract class AbstractStationDayChart extends AbstractStationChart{    abstract protected function onMeasurementRow(MeasurementDayRow $measurementDayRow);    public function getContent()    {        $n = 0;        foreach ($this->stationRowList as $stationRow) {            $this->onStationRow($stationRow);            $reader = new MeasurementDayReader();            $reader->filter->andEqual($reader->model->stationId, $stationRow->id);            if ($this->dateFrom !== null) {                $reader->filter->andEqualOrGreater($reader->model->date, $this->dateFrom->getIsoDate());            }            if ($this->dateTo !== null) {                $reader->filter->andEqualOrSmaller($reader->model->date, $this->dateTo->getIsoDate());            }            $reader->addOrder($reader->model->date);            foreach ($reader->getData() as $measurementRow) {                $this->onMeasurementRow($measurementRow);                if ($n === 0) {                    $this->addXAxisLabel($measurementRow->date->getShortDateLeadingZeroFormat());                }            }            $n++;        }        return parent::getContent();    }}