<?phpnamespace Nemundo\Meteo\Isd\Com\Chart\Day;use Nemundo\Com\Chart\Data\LineChartData;use Nemundo\Meteo\Isd\Data\MeasurementDay\MeasurementDayRow;use Nemundo\Meteo\Isd\Data\Station\StationRow;class QnhDayChart extends AbstractStationDayChart{    /**     * @var LineChartData[]     */    private $temperatureMaxData = [];    protected function loadContainer()    {        parent::loadContainer();        $this->chartTitle = 'QNH (per Day)';        $this->yMinValue=980;        $this->yMaxValue=1050;    }    protected function onStationRow(StationRow $stationRow)    {        $this->temperatureMaxData[$stationRow->id] =new LineChartData($this);        $this->temperatureMaxData[$stationRow->id]->legend = $stationRow->station;    }    protected function onMeasurementRow(MeasurementDayRow $measurementDayRow)    {        $this->temperatureMaxData[$measurementDayRow->stationId]->addValue($measurementDayRow->qnh);    }}