<?phpnamespace Nemundo\Meteo\Isd\Com\Chart\Day;use Nemundo\Com\Chart\Data\LineChartData;use Nemundo\Meteo\Isd\Data\MeasurementDay\MeasurementDayRow;use Nemundo\Meteo\Isd\Data\Station\StationRow;class TemperatureDayChart extends AbstractStationDayChart{    /**     * @var LineChartData[]     */    private $temperatureMaxData = [];    /**     * @var LineChartData[]     */    private $temperatureMinData = [];    protected function loadContainer()    {        parent::loadContainer();        $this->chartTitle = 'Temperature (per Day)';    }    protected function onStationRow(StationRow $stationRow)    {        $this->temperatureMaxData[$stationRow->id] = new LineChartData($this);        $this->temperatureMaxData[$stationRow->id]->legend = $stationRow->station.' (Max)';        $this->temperatureMinData[$stationRow->id] = new LineChartData($this);        $this->temperatureMinData[$stationRow->id]->legend = $stationRow->station.' (Min)';    }    protected function onMeasurementRow(MeasurementDayRow $measurementDayRow)    {        $this->temperatureMaxData[$measurementDayRow->stationId]->addValue($measurementDayRow->temperatureMax);        $this->temperatureMinData[$measurementDayRow->stationId]->addValue($measurementDayRow->temperatureMin);    }}