<?phpnamespace Nemundo\Meteo\Isd\Com\Chart\Hour;use Nemundo\Com\Chart\Data\BarChartData;use Nemundo\Meteo\Isd\Data\Measurement\MeasurementRow;use Nemundo\Meteo\Isd\Data\Station\StationRow;class PrecipitationHourChart extends AbstractStationHourChart{    /**     * @var BarChartData[]     */    private $line = [];    protected function loadContainer()    {        parent::loadContainer();        $this->chartTitle = 'Precipitation';    }    protected function onStationRow(StationRow $stationRow)    {        $this->line[$stationRow->id] = new BarChartData($this);        $this->line[$stationRow->id]->legend = $stationRow->station;    }    protected function onMeasurementRow(MeasurementRow $measurementRow)    {        $value = null;        if ($measurementRow->precipitationValid) {            $value = $measurementRow->precipitation;        }        $this->line[$measurementRow->stationId]->addValue($value);        //$this->temperatureData[$measurementRow->stationId]->addValue($measurementRow->precipitation);    }}