<?phpnamespace Nemundo\Meteo\Isd\Com\Chart\Hour;use Nemundo\Com\Chart\Data\LineChartData;use Nemundo\Meteo\Isd\Data\Measurement\MeasurementRow;use Nemundo\Meteo\Isd\Data\Station\StationRow;class TemperatureDewPointHourChart extends AbstractStationHourChart{    /**     * @var LineChartData[]     */    private $temperatureData = [];    /**     * @var LineChartData[]     */    private $dewPointData = [];    protected function loadContainer()    {        parent::loadContainer();        $this->chartTitle = 'Temperature/Dew Point';    }    protected function onStationRow(StationRow $stationRow)    {        $this->temperatureData[$stationRow->id] = new LineChartData($this);        $this->temperatureData[$stationRow->id]->legend = $stationRow->station;        $this->dewPointData[$stationRow->id] = new LineChartData($this);        //$this->dewPointData[$stationRow->id]->legend = $stationRow->station;    }    protected function onMeasurementRow(MeasurementRow $measurementRow)    {        $this->temperatureData[$measurementRow->stationId]->addValue($measurementRow->temperature);        $this->dewPointData[$measurementRow->stationId]->addValue($measurementRow->dewPoint);    }}