<?phpnamespace Nemundo\Meteo\Isd\Com\Chart\Hour;use Nemundo\Com\Chart\Data\LineChartData;use Nemundo\Meteo\Isd\Data\Measurement\MeasurementRow;use Nemundo\Meteo\Isd\Data\Station\StationRow;class TemperatureHourChart extends AbstractStationHourChart{    /**     * @var LineChartData[]     */    private $temperatureData = [];    protected function loadContainer()    {        parent::loadContainer();        $this->chartTitle = 'Temperature';    }    protected function onStationRow(StationRow $stationRow)    {        $this->temperatureData[$stationRow->id] = new LineChartData($this);        $this->temperatureData[$stationRow->id]->legend = $stationRow->station;    }    protected function onMeasurementRow(MeasurementRow $measurementRow)    {        $value = null;        if ($measurementRow->temperatureValid) {            $value = $measurementRow->temperature;        }        $this->temperatureData[$measurementRow->stationId]->addValue($value);    }}