<?phpnamespace Nemundo\Meteo\Isd\Com\Chart\Hour;use Nemundo\Com\Chart\Data\ScatterData;use Nemundo\Meteo\Isd\Data\Measurement\MeasurementRow;use Nemundo\Meteo\Isd\Data\Station\StationRow;class WindDirectionHourChart extends AbstractStationHourChart{    /**     * @var ScatterData[]     */    private $chartData = [];    protected function loadContainer()    {        parent::loadContainer();        $this->chartTitle = 'Wind Direction';    }    protected function onStationRow(StationRow $stationRow)    {        $this->chartData[$stationRow->id] = new ScatterData($this);        $this->chartData[$stationRow->id]->legend = $stationRow->station;    }    protected function onMeasurementRow(MeasurementRow $measurementRow)    {        $value = null;        if ($measurementRow->windDirectionValid) {            $value = $measurementRow->windDirection;        }        $this->chartData[$measurementRow->stationId]->addValue($value);    }}