<?phpnamespace Nemundo\Meteo\Isd\Com\Chart;use Nemundo\Core\Debug\Debug;use Nemundo\Meteo\Isd\Data\Measurement\MeasurementRow;use Nemundo\Meteo\Isd\Data\MeasurementDay\MeasurementDayRow;use Nemundo\Meteo\Isd\Data\Station\StationRow;use Nemundo\Package\Echarts\Chart\AbstractDataChart;use Nemundo\Package\Echarts\Data\LineChartData;class TemperatureMaxDewPointDayChart extends AbstractStationDayChart{    /**     * @var LineChartData[]     */    private $temperatureMaxData = [];    /**     * @var LineChartData[]     */    //private $temperatureMinData = [];    /**     * @var LineChartData[]     */    private $dewPointData = [];    protected function loadContainer()    {        parent::loadContainer();        $this->chartTitle = 'Temperature/Dew Point (per Day)';    }    protected function onStationRow(StationRow $stationRow)    {        $this->temperatureMaxData[$stationRow->id] = new LineChartData($this);        $this->temperatureMaxData[$stationRow->id]->legend = 'Max Temperature';  // $stationRow->station;        $this->dewPointData[$stationRow->id] = new LineChartData($this);        $this->dewPointData[$stationRow->id]->legend = 'Dew Point';    }    protected function onMeasurementRow(MeasurementDayRow $measurementDayRow)    {       // (new Debug())->write($measurementDayRow->temperatureMax);        $this->temperatureMaxData[$measurementDayRow->stationId]->addValue($measurementDayRow->temperatureMax);    //    $this->temperatureMinData[$measurementDayRow->stationId]->addValue($measurementDayRow->temperatureMin);        $this->dewPointData[$measurementDayRow->stationId]->addValue($measurementDayRow->dewPointMax);    }}