<?phpnamespace Nemundo\Meteo\Isd\Com\Form;use Nemundo\Meteo\Isd\Data\DownloadQueue\DownloadQueue;use Nemundo\Package\Bootstrap\Form\BootstrapForm;class DownloadQueueForm extends BootstrapForm{    public $stationId;    public function getContent()    {        $this->submitButton->label = 'Download Queue';        return parent::getContent();    }    protected function onSubmit()    {        $data = new DownloadQueue();        $data->stationId = $this->stationId;        $data->year = 2020;        $data->finished = false;        $data->save();        exit;    }}