<?phpnamespace Nemundo\Meteo\Isd\Com\ListBox;use Nemundo\Meteo\Isd\Data\Country\CountryReader;use Nemundo\Meteo\Isd\Parameter\CountryParameter;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class CountryListBox extends BootstrapListBox{    protected function loadContainer()    {        parent::loadContainer();        $this->name = (new CountryParameter())->getParameterName();    }    public function getContent()    {        $this->label = 'Country';        $reader = new CountryReader();        $reader->addOrder($reader->model->country);        foreach ($reader->getData() as $countryRow) {            $this->addItem($countryRow->id, $countryRow->country . ' (' . $countryRow->countryCode . ')');        }        return parent::getContent();    }}