<?phpnamespace Nemundo\Meteo\Isd\Com\ListBox;use Nemundo\Meteo\Isd\Data\Station\StationReader;use Nemundo\Meteo\Isd\Data\Station\StationRow;use Nemundo\Meteo\Isd\Parameter\StationTextParameter;use Nemundo\Meteo\Isd\Site\Json\StationAutocompleteSite;use Nemundo\Package\Bootstrap\Autocomplete\BootstrapAutocompleteTextBox;class StationAutocompleteListBox extends BootstrapAutocompleteTextBox{    protected function loadContainer()    {        parent::loadContainer();        $this->label = 'Station';        $this->name = (new StationTextParameter())->getParameterName();    }    public function getContent()    {        $this->sourceSite = StationAutocompleteSite::$site;        return parent::getContent();    }    public function getStationId()    {        $stationId = null;        $reader = new StationReader();        $reader->filter->andEqual($reader->model->station, $this->getValue());        foreach ($reader->getData() as $stationRow) {            $stationId = $stationRow->id;        }        return $stationId;    }    public function getStationRow()    {        /** @var StationRow $stationRow */        $value = null;        $reader = new StationReader();        $reader->filter->andEqual($reader->model->station, $this->getValue());        foreach ($reader->getData() as $stationRow) {            $value = $stationRow;        }        return $value;    }}