<?phpnamespace Nemundo\Meteo\Isd\Com\ListBox;use Nemundo\Core\Structure\ForLoop;use Nemundo\Core\Type\DateTime\Date;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class YearListBox extends BootstrapListBox{    protected function loadContainer()    {        parent::loadContainer();        $this->label = 'Year';        $loop = new ForLoop();        $loop->minNumber = 1900;  // 2000;        $loop->maxNumber = (new Date())->setNow()->getYear();  // 2020;        foreach ($loop->getData() as $year) {            $this->addItem($year, $year);        }    }}