<?phpnamespace Nemundo\Meteo\Isd\Com\Table;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Com\TableBuilder\TableRow;use Nemundo\Core\Type\DateTime\Date;use Nemundo\Meteo\Isd\Data\Measurement\MeasurementReader;class MeasurementTable extends AdminTable{    public $stationId;    /**     * @var Date     */    public $dateFrom;    /**     * @var Date     */    public $dateUntil;    public function getContent()    {        $reader = new MeasurementReader();        $reader->filter->andEqual($reader->model->stationId, $this->stationId);        $reader->filter->andEqualOrGreater($reader->model->date, $this->dateFrom->getIsoDate());        $reader->filter->andEqualOrSmaller($reader->model->date, $this->dateUntil->getIsoDate());        $reader->addOrder($reader->model->id);        $header=new TableHeader($this);        $header->addText($reader->model->dateTime->label);        $header->addText($reader->model->temperature->label);        foreach ($reader->getData() as $measurementRow) {            $row=new TableRow($this);            $row->addText($measurementRow->dateTime->getIsoDateTime());            $row->addText($measurementRow->temperature);        }        return parent::getContent();    }}