<?phpnamespace Nemundo\Meteo\Isd\Import;use Nemundo\Core\Base\AbstractImport;use Nemundo\Core\Csv\Reader\FixWidthCsvReader;use Nemundo\Meteo\Base\Download\FileDownload;use Nemundo\Meteo\Isd\Data\Country\Country;use Nemundo\Meteo\Isd\Data\Country\CountryBulk;class CountryImport extends AbstractImport{    public function importData()    {        $download = new FileDownload();        $download->url = 'ftp://ftp.ncdc.noaa.gov/pub/data/noaa/country-list.txt';        $download->filename = 'country-list.txt';        $filename = $download->downloadFile();        $csvReader = new FixWidthCsvReader();        $csvReader->filename = $filename;        $csvReader->addSeperationPosition(12);        $csvReader->useFirstRowAsHeader = true;        $csvReader->lineOfStart = 2;        $data=new CountryBulk();        //$data->updateOnDuplicate = true;        $data->ignoreIfExists = true;        foreach ($csvReader->getData() as $csvRow) {            //$data = new Country();            $data->countryCode = $csvRow[0];            $data->country = $csvRow[1];            $data->save();        }        $data->saveBulk();    }}