ta->dewPoint = $dewPoint / 10;                    $data->dewPointValid = true;                }                $qnh = $text->getSubstring(25, 6);                $data->qnh=0;                $data->qnhValid = false;                if ($qnh !== '-9999') {                    $qnh = $qnh / 10;                    $data->qnh=$qnh;                    $data->qnhValid = true;                    //$qnhTimeSeries->addDayData($data->date,$this->stationRow->station,$qnh);                }                $windDirection = $text->getSubstring(31, 6);                $data->windDirection=0;                $data->windDirectionValid = false;                if ($windDirection !== '-9999') {                    $data->windDirection = $windDirection;                    $data->windDirectionValid = true;                }                $wind = $text->getSubstring(37, 6);                $data->wind=0;                $data->windValid = false;                if ($wind !== '-9999') {                    $wind = (($wind / 10) * 60 * 60) / 1000;                    $data->wind = $wind;                    $data->windValid = true;                    //$windTimeSeries->addDayData($data->date,$this->stationRow->station,$wind);                }                $precipitation = $text->getSubstring(50, 6);                $data->precipitation=0;                $data->precipitationValid = false;                if ($precipitation !== '-9999') {                    $data->precipitation = $precipitation / 10;                    $data->precipitationValid = true;                }                $data->save();            }        }        $data->saveBulk();        $import = new MeasurementDayImport();        $import->stationId = $this->stationId;        $import->year= $this->year;        $import->importData();        /*        $temperatureTimeSeries->saveAverageFromDate();        $qnhTimeSeries->saveAverageFromDate();        $windTimeSeries->saveAverageFromDate();*/        //exit;        /*        $measurementReader = new MeasurementReader();        $measurementReader->filter->andEqual($measurementReader->model->stationId, $this->stationRow->id);        $measurementReader->addGroup($measurementReader->model->date);        $max=new MaxField($measurementReader);        $max->aggregateField=$measurementReader->model->temperature;        $min=new MinField($measurementReader);        $min->aggregateField=$measurementReader->model->temperature;*/        /*        foreach ($measurementReader->getData() as $measurementRow) {            //(new Debug())->write($measurementRow->getModelValue($max));            $data = new MeasurementDay();            $data->ignoreIfExists=true;            $data->stationId = $this->stationRow->id;            $data->date = $measurementRow->date;            $measurementReader2 = new MeasurementReader();            $measurementReader2->filter->andEqual($measurementReader2->model->stationId, $this->stationRow->id);            $measurementReader2->filter->andEqual($measurementReader2->model->date, $measurementRow->date->getIsoDateFormat());            $measurementReader2->addOrder($measurementReader2->model->temperature, SortOrder::DESCENDING);            $measurementReader2->limit = 1;            foreach ($measurementReader2->getData() as $measurementRow2) {                $data->temperatureMax = $measurementRow2->temperature;                $data->dewPointMax = $measurementRow2->dewPoint;            }            $measurementReader2 = new MeasurementReader();            $measurementReader2->filter->andEqual($measurementReader2->model->stationId, $this->stationRow->id);            $measurementReader2->filter->andEqual($measurementReader2->model->date, $measurementRow->date->getIsoDateFormat());            $measurementReader2->addOrder($measurementReader2->model->temperature, SortOrder::ASCENDING);            $measurementReader2->limit = 1;        