<?phpnamespace Nemundo\Meteo\Isd\Install;use Nemundo\App\Application\Setup\ApplicationSetup;use Nemundo\App\Scheduler\Setup\SchedulerSetup;use Nemundo\App\Script\Setup\ScriptSetup;use Nemundo\Content\App\Job\Application\JobApplication;use Nemundo\Content\App\Job\Setup\JobSetup;use Nemundo\Content\Application\ContentApplication;use Nemundo\Content\Index\Geo\Application\GeoIndexApplication;use Nemundo\Content\Index\Geo\Setup\GeoContentTypeSetup;use Nemundo\Content\Setup\ContentTypeSetup;use Nemundo\Meteo\Isd\Application\IsdApplication;use Nemundo\Meteo\Isd\Content\Station\StationContentType;use Nemundo\Meteo\Isd\Content\WindHour\WindHourContentType;use Nemundo\Meteo\Isd\Data\IsdModelCollection;use Nemundo\Meteo\Isd\Job\CountryStationImportJob;use Nemundo\Meteo\Isd\Job\DownloadJob;use Nemundo\Meteo\Isd\Job\Station\StationValueDownloadJob;use Nemundo\Meteo\Isd\Scheduler\DownloadScheduler;use Nemundo\Meteo\Isd\Script\CountryDataImportScript;use Nemundo\Meteo\Isd\Script\DownloadScript;use Nemundo\Meteo\Isd\Script\IsdCleanScript;use Nemundo\Meteo\Isd\Script\IsdImportScript;use Nemundo\Meteo\Isd\Script\IsdIndexScript;use Nemundo\Meteo\Isd\Script\IsdTestScript;use Nemundo\Meteo\Isd\Script\StationMeasurementImportScript;use Nemundo\Model\Setup\ModelCollectionSetup;use Nemundo\App\Application\Type\Install\AbstractInstall;class IsdInstall extends AbstractInstall{    public function install()    {        (new ContentApplication())->installApp();        (new GeoIndexApplication())->installApp();        //(new JobApplication())->installApp();        (new ApplicationSetup())            ->addApplication(new IsdApplication());        (new ModelCollectionSetup())            ->addCollection(new IsdModelCollection());        (new ScriptSetup(new IsdApplication()))            ->addScript(new IsdCleanScript())            ->addScript(new IsdImportScript())            ->addScript(new CountryDataImportScript())            ->addScript(new St