<?phpnamespace Nemundo\Meteo\Isd\Job\Station;use Nemundo\Content\App\Job\Content\AbstractJobContentType;use Nemundo\Meteo\Isd\Data\DownloadQueue\DownloadQueueReader;use Nemundo\Meteo\Isd\Data\DownloadQueue\DownloadQueueRow;use Nemundo\Meteo\Isd\Import\MeasurementImport;class StationValueDownloadJob extends AbstractJobContentType{    protected function loadContentType()    {        $this->typeLabel = 'Station Value Import';        $this->typeId = 'a225e8ad-0a1a-4336-a9f7-b26dcc7ca05b';        $this->formClassList[] = StationValueDownloadQueueForm::class;        // TODO: Implement loadContentType() method.    }    protected function onDataRow()    {        $reader = new DownloadQueueReader();        $reader->model->loadStation()->station->loadCountry();        $this->dataRow = $reader->getRowById($this->getDataId());    }    /**     * @return \Nemundo\Model\Row\AbstractModelDataRow|DownloadQueueRow     */    public function getDataRow()    {        return parent::getDataRow(); // TODO: Change the autogenerated stub    }    public function getSubject()    {        $subject = $this->getDataRow()->station->station . ' ' . $this->getDataRow()->year;        return $subject;        //return parent::getSubject(); // TODO: Change the autogenerated stub    }    public function run()    {        $queueRow = $this->getDataRow();        $download = new MeasurementImport();        $download->stationRow = $queueRow->station;        $download->year = $queueRow->year;        $download->importData();    }}