<?phpnamespace Nemundo\Meteo\Isd\Page;use Nemundo\Admin\Com\Button\AdminSiteButton;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Com\TableBuilder\TableRow;use Nemundo\Meteo\Isd\Com\Form\CountryDownloadQueueForm;use Nemundo\Meteo\Isd\Com\Form\StationDownloadQueueForm;use Nemundo\Meteo\Isd\Data\DownloadQueue\DownloadQueueReader;use Nemundo\Meteo\Isd\Site\Download\DownloadDeleteSite;use Nemundo\Meteo\Isd\Template\IsdTemplate;use Nemundo\Package\Bootstrap\Layout\BootstrapTwoColumnLayout;class DownloadQueuePage extends IsdTemplate{    public function getContent()    {        $btn = new AdminSiteButton($this);        $btn->site = DownloadDeleteSite::$site;        $layout = new BootstrapTwoColumnLayout($this);        new CountryDownloadQueueForm($layout->col1);        new StationDownloadQueueForm($layout->col2);        $table = new AdminTable($this);        $reader = new DownloadQueueReader();        $reader->model->loadStation();        $reader->addOrder($reader->model->finished);        $reader->addOrder($reader->model->id);        $header = new TableHeader($table);        $header->addText($reader->model->station->label);        $header->addText($reader->model->station->stationCode->label);        $header->addText($reader->model->year->label);        $header->addText($reader->model->finished->label);        foreach ($reader->getData() as $downloadQueueRow) {            $row = new TableRow($table);            $row->addText($downloadQueueRow->station->station);            $row->addText($downloadQueueRow->station->stationCode);            $row->addText($downloadQueueRow->year);            $row->addYesNo($downloadQueueRow->finished);        }        return parent::getContent();    }}