<?phpnamespace Nemundo\Meteo\Isd\Reader;use Nemundo\Core\Base\DataSource\AbstractDataSource;use Nemundo\Meteo\Isd\Data\Station\StationReader;class IsdStationReader extends AbstractDataSource{    public $countryCode;    protected function loadData()    {        $reader=new StationReader();        $reader->model->loadCountry();        $reader->filter->andEqual($reader->model->country->countryCode,$this->countryCode);        $reader->addOrder($reader->model->station);        foreach ($reader->getData() as $stationRow) {            $item=new IsdStationItem();            $item->id=$stationRow->id;            $item->stationCode=$stationRow->stationCode;            $item->station=$stationRow->station;            $this->addItem($item);        }    }    /**     * @return IsdStationItem[]     */    public function getData()    {        return parent::getData(); // TODO: Change the autogenerated stub    }}