<?phpnamespace Nemundo\Meteo\Isd\Script;use Nemundo\App\Script\Type\AbstractConsoleScript;use Nemundo\Content\Builder\IndexBuilder;use Nemundo\Core\Debug\Debug;use Nemundo\Meteo\Isd\Content\Station\StationContentType;use Nemundo\Meteo\Isd\Data\Station\StationPaginationReader;class IsdIndexScript extends AbstractConsoleScript{    public function loadScript()    {        $this->scriptName = 'isd-index';    }    public function run()    {        $page = 0;        do {            (new Debug())->write($page);            $reader = new StationPaginationReader();            $reader->currentPage=$page;            $reader->paginationLimit=10;            foreach ($reader->getData() as $row) {                $type = new StationContentType($row->id);                (new IndexBuilder())->buildIndex($type);            }            $page++;        } while ($reader->getCount() > 0);    }}