<?phpnamespace Nemundo\Meteo\Isd\Script;use Nemundo\App\Script\Type\AbstractConsoleScript;use Nemundo\Core\Console\ConsoleInput;use Nemundo\Core\Type\DateTime\DateTime;use Nemundo\Meteo\Isd\Data\Measurement\Measurement;use Nemundo\Meteo\Isd\Data\Measurement\MeasurementDelete;use Nemundo\Meteo\Isd\Data\Station\StationReader;use Nemundo\Meteo\Isd\Import\MeasurementImport;class StationMeasurementImportScript extends AbstractConsoleScript{    protected function loadScript()    {        $this->scriptName = 'isd-station';    }    public function run()    {        $input = new ConsoleInput();        $input->message = 'Station Code';        $input->defaultValue = '066500';        $stationCode = $input->getValue();        $input = new ConsoleInput();        $input->message = 'Year Begin';        $input->defaultValue = '2020';        $yearBegin = $input->getValue();        //$stationCode ='160760';        //$yearBegin='2020';        /*        $input = new ConsoleInput();        $input->message = 'Year End';        $input->defaultValue = '2019';        $yearEnd = $input->getValue();*/        $stationReader = new StationReader();        $stationReader->model->loadCountry();        $stationReader->filter->andEqual($stationReader->model->stationCode, $stationCode);        $stationRow = $stationReader->getRow();        $download = new MeasurementImport();        $download->stationRow = $stationRow;        $download->year = $yearBegin;        $download->importData();    }}