<?phpnamespace Nemundo\Meteo\Isd\Site;use Nemundo\Meteo\Isd\Page\IsdPage;use Nemundo\Meteo\Isd\Site\Json\StationAutocompleteSite;use Nemundo\Meteo\Isd\Site\Kml\StationKmlSite;use Nemundo\Web\Site\AbstractSite;class IsdSite extends AbstractSite{    /**     * @var IsdSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Integrated Surface Database (ISD)';        $this->url = 'isd';        new StationKmlSite($this);        new StationItemSite($this);        new StationAutocompleteSite($this);        new StationSearchSite($this);        /*        new StationComparisonSite($this);        new TempSite($this);        new SessionClearSite($this);*/        new CountrySite($this);        new DownloadQueueSite($this);        new SaveDownloadQueueSite($this);        IsdSite::$site = $this;    }    public function loadContent()    {        (new IsdPage())->render();    }}