<?phpnamespace Nemundo\Meteo\Isd\Site;use Nemundo\Meteo\Isd\Data\DownloadQueue\DownloadQueue;use Nemundo\Meteo\Isd\Parameter\StationParameter;use Nemundo\Package\FontAwesome\Site\AbstractEditIconSite;use Nemundo\Package\FontAwesome\Site\AbstractSaveIconSite;use Nemundo\Web\Site\AbstractSite;use Nemundo\Core\Http\Url\UrlReferer;class SaveDownloadQueueSite extends AbstractSaveIconSite{    /**     * @var SaveDownloadQueueSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Save Download Queue';        $this->url = 'save-download-queue';        SaveDownloadQueueSite::$site = $this;    }    public function loadContent()    {        $data = new DownloadQueue();        $data->stationId = (new StationParameter())->getValue();        $data->year = 2020;        $data->finished = false;        $data->save();        (new UrlReferer())->redirect();    }}