<?phpnamespace Nemundo\Meteo\Isd\Site;use Nemundo\Meteo\Isd\Page\StationSearchPage;use Nemundo\Web\Site\AbstractSite;class StationSearchSite extends AbstractSite{    /**     * @var StationSearchSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Station Search';        $this->url = 'station-search';        StationSearchSite::$site = $this;    }    public function loadContent()    {        (new StationSearchPage())->render();    }}