<?phpnamespace Nemundo\Meteo\Isd\Type;use Nemundo\Core\Base\AbstractBase;use Nemundo\Meteo\Isd\Data\Country\CountryId;class CountryType extends AbstractBase{    public function getCountryId($countryCode)    {        $id = new CountryId();        $id->filter->andEqual($id->model->countryCode, $countryCode);        $countryid = $id->getId();        return $countryid;    }}