<?phpnamespace Nemundo\Meteo\Isd\Utility;use Nemundo\Core\Type\DateTime\Date;use Nemundo\Core\Type\Text\Text;class SixDigitDate extends Date{    public function fromSixDigit($sixDigitDate)    {        $text = new Text($sixDigitDate);        $this->setYear($text->getSubstring(0, 4));        $this->setMonth($text->getSubstring(4, 2));        $this->setDay($text->getSubstring(6, 2));        return $this;    }}