<?phpnamespace Nemundo\Meteo\Isd\Type;use Nemundo\Core\Base\AbstractBase;use Nemundo\Meteo\Isd\Data\Station\StationReader;class StationType extends AbstractBase{    public function getStationRow($stationCode) {        $reader=new StationReader();        $reader->filter->andEqual($reader->model->stationCode,$stationCode);        return $reader->getRow();    }}