<?phpnamespace Nemundo\Meteo\Kachelmann\Import;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\Date\DayReader;use Nemundo\Core\Type\DateTime\Date;use Nemundo\Core\Type\DateTime\DateTime;use Nemundo\Core\WebRequest\CurlWebRequest;use Nemundo\Meteo\Kachelmann\Data\Satellitenbilder\Satellitenbilder;use Nemundo\Project\Path\TmpPath;class SatellitenbilderImport extends AbstractBase{    /**     * @var Date     */    public $dateFrom;    /**     * @var Date     */    public $dateTo;    public function importImage() {        //$url = 'https://img4.kachelmannwetter.com/images/data/cache/sat/download_sat-de-310-1-zz_2020_05_15_18_45_1431_128.png';        //https://img1.kachelmannwetter.com/images/data/cache/sat/download_sat-de-310-1-zz_2020_05_16_19_00_1431_128.png        // https://img1.kachelmannwetter.com/images/data/cache/sat/download_sat-de-310-1-zz_2020_05_16_19_45_1431_128.png        // https://img1.kachelmannwetter.com/images/data/cache/sat/download_sat-de-310-1-zz_2021_05_01_14_00_5428_128.png        $dayReader=new DayReader();        $dayReader->dateFrom = $this->dateFrom;        $dayReader->dateTo = $this->dateTo;        foreach ($dayReader->getData() as $date) {            $dateTime = (new DateTime())->fromDate($date);            $dateTime->setHour(14);            $dateTime->setMinute(0);            $url = 'https://img1.kachelmannwetter.com/images/data/cache/sat/download_sat-de-310-1-zz_'.                $date->getYear().'_'.                $date->getMonthNumberWithLeadingZero().'_'.                $date->getDayWithLeadingZero().'_'.                $dateTime->getHourWithLeadingZero().'_'.                $dateTime->getMinuteWithLeadingZero().'_5428_128.png';            $filename = (new TmpPath())                ->addPath($dateTime->getIsoDate().'.png')                ->getFullFilename();            $webRequest = new CurlWebRequest();            $webRequest->referrerUrl='https://kachelmannwetter.com/ch/sat/europa-afrika/satellit-infrarot-15min.html';            $webRequest->downloadUrl($url,$filename);            $webRequest->delayRequest=true;            $webRequest->delayInSecond=2;            /*            $data=new Satellitenbilder();            $data->dateTime=$dateTime;            $data->image->fromUrl($url);            $data->save();*/        }    }}