<?phpnamespace Nemundo\Meteo\Meteocentrale\Content\Bisendiagramm;use Nemundo\Db\Sql\Order\SortOrder;use Nemundo\Html\Formatting\Small;use Nemundo\Meteo\Meteocentrale\Com\MeteocentraleSourceSmall;use Nemundo\Meteo\Meteocentrale\Data\Bisendiagramm\BisendiagrammReader;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;use Nemundo\Package\Bootstrap\Layout\Grid\BootstrapRow;use Nemundo\Content\View\AbstractContentView;class BisendiagrammContentView extends AbstractContentView{    protected function loadView()    {        $this->viewName='default';        $this->viewId='1893d257-df3b-474a-9607-14031892663c';        $this->defaultView=true;    }    public function getContent()    {        $bisendiagrammReader = new BisendiagrammReader();        //$bisendiagrammReader->addOrder($bisendiagrammReader->model->dateTime, SortOrder::DESCENDING);        $bisendiagrammReader->addOrder($bisendiagrammReader->model->id, SortOrder::DESCENDING);        $bisendiagrammReader->limit = 1;        foreach ($bisendiagrammReader->getData() as $bisendiagrammRow) {            $row = new BootstrapRow($this);            $img = new BootstrapResponsiveImage($row);            $img->src = $bisendiagrammRow->bisendiagramm->getUrl();        }        new MeteocentraleSourceSmall($this);        return parent::getContent();    }}