<?phpnamespace Nemundo\Meteo\Meteocentrale\Content\Foehndiagramm;use Nemundo\Db\Sql\Order\SortOrder;use Nemundo\Html\Formatting\Small;use Nemundo\Meteo\Meteocentrale\Com\MeteocentraleSourceSmall;use Nemundo\Meteo\Meteocentrale\Data\Foehndiagramm\FoehndiagrammReader;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;use Nemundo\Package\Bootstrap\Layout\Grid\BootstrapRow;use Nemundo\Content\View\AbstractContentView;class FoehndiagrammContentView extends AbstractContentView{    protected function loadView()    {        $this->viewName='default';        $this->viewId='ef963538-9305-4b1d-8f63-8c1b7416eda4';        $this->defaultView=true;    }    public function getContent()    {        $foehndiagrammReader = new FoehndiagrammReader();        $foehndiagrammReader->addOrder($foehndiagrammReader->model->id, SortOrder::DESCENDING);        $foehndiagrammReader->limit = 1;        foreach ($foehndiagrammReader->getData() as $foehndiagrammRow) {            $row = new BootstrapRow($this);            $img = new BootstrapResponsiveImage($row);            $img->src = $foehndiagrammRow->foehndiagramm->getUrl();        }        new MeteocentraleSourceSmall($this);        return parent::getContent();    }}