<?phpnamespace Nemundo\Meteo\Meteocentrale\Data\Foehndiagramm;class FoehndiagrammReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var FoehndiagrammModel*/public $model;public function __construct() {parent::__construct();$this->model = new FoehndiagrammModel();}/*** @return FoehndiagrammRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return FoehndiagrammRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return FoehndiagrammRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new FoehndiagrammRow($dataRow, $this->model);$row->model = $this->model;return $row;}}