<?phpnamespace Nemundo\Meteo\Meteociel\Com\Image;use Nemundo\Core\Type\DateTime\DateTime;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;class MeteocielSatImage extends BootstrapResponsiveImage{    /**     * @var DateTime     */    public $dateTime;    public function getContent()    {        // http://neige.meteociel.fr/satellite/archives/15-07-2020/satvis-10-00.jpg        // http://neige.meteociel.fr/satellite/archives/15-07-2020/satvisit-10-00.jpg        $country = '';        $url = 'http://neige.meteociel.fr/satellite/archives/'.            $this->dateTime->getDayWithLeadingZero().'-'.            $this->dateTime->getMonthNumberWithLeadingZero().'-'.            $this->dateTime->getYear().            '/satvis'.$country.'-'.$this->dateTime->getHour().'-'.$this->dateTime->getMinute().'.jpg';        $this->src=$url;        return parent::getContent();    }}