<?phpnamespace Nemundo\Meteo\MeteoschweizAllgemeineLage\Content\AllgemeineLage;use Nemundo\Core\Type\Text\Html;use Nemundo\Html\Formatting\Bold;use Nemundo\Html\Paragraph\Paragraph;use Nemundo\Content\View\AbstractContentView;class AllgemeineLageContentView extends AbstractContentView{    /**     * @var AllgemeineLageContentType     */    public $contentType;    protected function loadView()    {        $this->viewName='default';        $this->viewId='e76ffdb4-703e-4eb4-b64e-5240e6b24bd7';        $this->defaultView=true;    }    public function getContent()    {        $allgmeineLage = $this->contentType->getDataRow();        $bold = new Bold($this);        $bold->content = $allgmeineLage->datumText;        $p = new Paragraph($this);        $p->content = (new Html($allgmeineLage->allgemeineLage))->getValue();        return parent::getContent(); // TODO: Change the autogenerated stub    }}